sqlquery.select:
SELECT ROUND(( CASE WHEN ( SELECT COUNT (*) FROM NGCS_WF_CMPLNTS_ARC_DETAIL WHERE SRV_REQST_TYPE_FULL_NM LIKE '集团业务→产品质量→物联卡→%' OR SRV_REQST_TYPE_FULL_NM LIKE '集团业务→网络质量→物联卡→%' OR SRV_REQST_TYPE_FULL_NM LIKE '集团业务→基础服务→物联卡→%'
) != 0 THEN (( SELECT COUNT (*) FROM NGCS_WF_CMPLNTS_ARC_DETAIL WHERE (SRV_REQST_TYPE_FULL_NM LIKE '集团业务→产品质量→物联卡→%' OR SRV_REQST_TYPE_FULL_NM LIKE '集团业务→网络质量→物联卡→%' OR SRV_REQST_TYPE_FULL_NM LIKE '集团业务→基础服务→物联卡→%'
) AND TOTAL_TIME3 < 48 )) / ( SELECT COUNT (*) FROM NGCS_WF_CMPLNTS_ARC_DETAIL WHERE SRV_REQST_TYPE_FULL_NM LIKE '集团业务→产品质量→物联卡→%' OR SRV_REQST_TYPE_FULL_NM LIKE '集团业务→网络质量→物联卡→%' OR SRV_REQST_TYPE_FULL_NM LIKE '集团业务→基础服务→物联卡→%'
) ELSE NULL END)*100,2) as value from dual
